mapper.update:

    UPDATE kb_entry SET order_number = order_number+1

    WHERE order_number >= #{currentOrderNumber} AND order_number<=#{originOrderNumber}




    UPDATE kb_entry SET order_number = order_number-1

    WHERE order_number <= #{currentOrderNumber} AND order_number>=#{originOrderNumber}



AND board_id=#{boardId} AND delete_status=0 AND id != #{id}
