mapper.update:

    UPDATE kb_task SET order_number = order_number+1

    WHERE order_number >= #{currentOrderNumber} AND order_number<=#{originOrderNumber}




    UPDATE kb_task SET order_number = order_number-1

    WHERE  order_number <= #{currentOrderNumber} AND order_number>=#{originOrderNumber}


AND entry_id=#{entryId} AND delete_status=0 AND id !=#{taskId}

UPDATE kb_task SET order_number = order_number+1

    WHERE order_number >= #{currentOrderNumber}

AND entry_id=#{currentEntryId} AND delete_status=0 AND id !=#{taskId}

UPDATE kb_task SET order_number = order_number-1

    WHERE order_number >= #{originOrderNumber}

AND entry_id=#{originEntryId} AND delete_status=0 AND id !=#{taskId}
